import React, {useContext} from 'react';
import { GlobalContext } from './context/GlobalState';


function moneyFormatter(num) {
    let p = num.toFixed(2).split('.');
    return (
      'R$ ' +
      p[0]
        .split('')
        .reverse()
        .reduce(function (acc, num, i, orig) {
          return num === '-' ? acc : num + (i && !(i % 3) ? ',' : '') + acc;
        }, '') +
      '.' +
      p[1]
    );
  }

export const GanhosDespesas = () => {

    const {transactions}= useContext(GlobalContext);

    const amounts = transactions.map(transaction => transaction.amount);

    const ganhos = amounts
     .filter(item => item > 0)
     .reduce((acc, item) => (acc += item), 0)
     

    const despesas = (
        amounts.filter(item => item <0).reduce((acc, item) => (acc += item), 0) * 
        -1
    );
    

    return (
        <div className="ganhos-despesas-container">
          <div>
              <h4>Ganhos</h4>
    <p className="money plus">{moneyFormatter(ganhos)}</p>
          </div>
          <div>
              <h4>Despesas</h4>
    <p className="money minus">{moneyFormatter (despesas)}</p>
         </div>
            
        </div>
    )
}

export default GanhosDespesas
